mapper.select:
select srr.* from (
select ts.*,rownum as rn from (
select sr.* from sys_role sr


		and sr.role_name like concat(concat('%',#{roleName}),'%') 


		and sr.create_user_id = #{createUserId} 


		and sr.remark = #{remark}




		order by ${sidx} ${order}


		order by sr.role_id asc


) ts ) srr


	 and srr.rn between #{offset} and #{limit}

select * from sys_role


		and create_user_id = #{createUserId}


order by role_id asc

select count(*) from sys_role 



		and role_name like concat(concat('%',#{roleName}),'%') 


		and create_user_id = #{createUserId} 


		and remark = #{remark}

mapper.update:
update sys_role 

	role_name = #{roleName}, 
	remark = #{remark},
	org_id = #{orgId}

where role_id = #{roleId}
